import React, { useEffect, useState } from "react";

function Game({ socket, setChannel }) {
  const [playersJoined, setPlayersJoined] = useState(false);
  const [userCount, setUserCount] = useState();
  

  useEffect(() => {
    socket.on("player_joined", (data) => {
      setUserCount(userCount + data);
      if (userCount > 1) {
        setPlayersJoined(true);
      }
    });
  }, [socket]);

  return (
    <>
      {!playersJoined && (
        <div>waiting for player to join</div>
      )}
      {playersJoined && (
        <div>Player joined, let's play</div>
      )}
    </>
  );
}

export default Game;
