import React, { useEffect, useState } from "react";
import ScrollToBottom from "react-scroll-to-bottom";

function Chat({ socket, username, room }) {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        room: room,
        author: username,
        message: currentMessage,
        time:
          new Date(Date.now()).getHours() +
          ":" +
          new Date(Date.now()).getMinutes(),
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
  }, [socket]);

  
return (
  <div className="chat-window container">
    <div className="chat-header row justify-content-center bg-primary text-white">
      <p className="h3">Live Chat</p>
    </div>
    <div className="chat-body row">
      <ScrollToBottom className="message-container col-12">
        {messageList.map((messageContent) => {
          return (
            <div
              className={`message ${username === messageContent.author ? 'my-message' : 'other-message'}`}
            >
              <div>
                <div className="message-content">
                  <p>{messageContent.message}</p>
                </div>
                <div className="message-meta d-flex justify-content-between align-items-center">
                  <p className="m-0">{messageContent.time}</p>
                  <p className="m-0">{messageContent.author}</p>
                </div>
              </div>
            </div>
          );
        })}
      </ScrollToBottom>
    </div>
    <div className="chat-footer row align-items-center">
      <input
        type="text"
        value={currentMessage}
        placeholder="Hey..."
        className="form-control col-10"
        onChange={(event) => {
          setCurrentMessage(event.target.value);
        }}
        onKeyPress={(event) => {
          event.key === "Enter" && sendMessage();
        }}
      />
      <button onClick={sendMessage} className="btn btn-primary col-2">&#9658;</button>
    </div>
  </div>
);

}

export default Chat;
