import React, { useEffect, useState } from "react";

function Game({ socket, username, room }) {
    const [userCount, setUserCount] = useState([]);
    
    useEffect(() => {
        socket.on("joined_game", (data) => {
      setUserCount(data.clientsInRoom);
      console.log(data.clientsInRoom + "From client")
    });
    socket.emit("join_game", { room : room });
  }, [socket]);
  

  return (
    <>
      {userCount < 2 && (
        <div>waiting for player to join</div>
      )}
      {userCount > 1 && (
        <div>Player joined, let's play</div>
      )}
    </>
  );
}

export default Game;
