import React, { useEffect, useState } from "react";

function Game({ socket, username, room }) {
  const [userCount, setUserCount] = useState();

  useEffect(() => {
    socket.on("player_joined", () => {
      const clientsInRoom = socket.adapter.rooms[room].clients;
      setUserCount(clientsInRoom.length);
    });
  }, [socket, room]);

  return (
    <>
      {userCount < 2 && (
        <div>waiting for player to join</div>
      )}
      {userCount === 2 && (
        <div>Player joined, let's play</div>
      )}
    </>
  );
}

export default Game;
