import React, { useState } from 'react';
import Chat from './components/Chat';
import LoginForm from './components/LoginForm';
import RegistrationForm from './components/RegistrationForm';
import UserList from './components/UserList';
import axios from 'axios';
import io from "socket.io-client"
import "./App.css";


function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState(false);
  const [oponentUser, setOponentUser] = useState(null);
  const[room,setRoom] = useState(5);
  const [showChat, setShowChat] = useState(false);



  const socket = io.connect("http://localhost:3000");

  const changeUsername = (newUsername) => {
    setUsername(newUsername);
  }

  const handleOponent = (oponent) =>{
      setOponentUser(oponent);
      socket.emit("join_room", room);
      setShowChat(true);
  }

  function handleLogout() {
    axios.post('http://localhost:3000/logout', {
      username
    })
      .then((response) => {
        if (response.data.success) {
          // Update the loggedIn state and hide the logout button
          setLoggedIn(false);
        } else {
          // Display an error message
        }
      })
      .catch((error) => {
        // Handle any errors
      });
  }

  return (
    <div>
      {!loggedIn && (
        <>
          <LoginForm onLogin={() => setLoggedIn(true)} changeUsername={changeUsername} />
          <RegistrationForm />
        </>
      )}
      {loggedIn && (
        <UserList oponent={oponentUser} clickedUser={handleOponent} />
      )}
      {loggedIn && (
        <button onClick={handleLogout}>Logout</button>
      )}

<div>
      {oponentUser !== null && (
        <div>
          <p>Oponent user: {oponentUser.username}</p>
        </div>
      )}
    </div>
        <Chat socket={socket} username={username} room={room} />
        {loggedIn && (
        <button onClick={handleLogout}>Logout</button>
      )}
    </div>


  );
}

export default App;
