import React, { useEffect, useState } from "react";

function Game({ socket, username, room }) {
  const [userCount, setUserCount] = useState();

  useEffect(() => {
    // Emit the joined_game event when the component is loaded
    socket.emit("joined_game", { room });
  
    // Listen for the joined_game event
    socket.on("joined_game", (data) => {
      setUserCount(data.userCount);
    });
  }, [socket, room]);
  

  return (
    <>
      {userCount < 2 && (
        <div>waiting for player to join</div>
      )}
      {userCount === 2 && (
        <div>Player joined, let's play</div>
      )}
    </>
  );
}

export default Game;
