import React, { useState, useEffect } from 'react';

function Game({ socket, room, isUserFirst }) {
    const [board, setBoard] = useState(Array(9).fill(null));
    const [currentPlayer, setCurrentPlayer] = useState('X');
    const [playerShape, setPlayerShape] = useState("O");
    
    useEffect(() => {
        
        
        socket.on("change_game", (data) => {
            setBoard(data.newBoard);
            setCurrentPlayer(currentPlayer === 'X' ? 'O' : 'X');
            
        });
        
        
    }, [socket]);
    if (isUserFirst === 2) {
        setPlayerShape("X");
    }


    const handleSquareClick = (index) => {

        if (board[index] !== null || currentPlayer !== playerShape) {
            return;
        }
        const newBoard = [...board];
        newBoard[index] = currentPlayer;
        setBoard(newBoard);
        socket.emit("updateGame", { room: room, newBoard: newBoard });
        setCurrentPlayer(currentPlayer === 'X' ? 'O' : 'X');
    };

    return (
        <div className="game">
            <h1>whos turn: {currentPlayer}</h1>
            <h1>you are: {playerShape}</h1>
            <h1>{isUserFirst}</h1>
            <div className="board">
                {board.map((square, index) => (
                    <div
                        key={index}
                        className="square"
                        onClick={() => handleSquareClick(index)}
                    >
                        {square}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Game;
