import React, { useEffect, useState } from "react";
import Chat from "./Chat";
import Game from "./Game";

function JoinGame({ socket, username, room }) {
    const [userCount, setUserCount] = useState([]);
    const [userNumber,setUserNumber]= useState(null);




    socket.emit("join_game", { room : room });
    
    useEffect(() => {
        socket.on("joined_game", (data) => {
      setUserCount(data.clientsInRoom);
      if(userNumber = null){
        setUserNumber(data.clientsInRoom)
        console.log(userNumber);
      }
      console.log(data.clientsInRoom + "From client")
    });
  }, [socket]);
  

  return (
    <>
      {userCount < 2 && (
        <div>waiting for player to join...
          <h2></h2>
        </div>
      )}
      {userCount < 2 && (
        <div>
            Player joined, let's play
            <Chat socket={socket} username={username} room={room} />
            <Game socket={socket} room={room} userNumber={userNumber}/>
        </div>



      )}
    </>
  );
}

export default JoinGame;
