import React, { useEffect, useState } from "react";

function Game({ socket, username, room }) {
  const [userCount, setUserCount] = useState(0);

  useEffect(() => {
    socket.on("joined_game", (data) => {
      setUserCount(data.user);
    });
  }, [socket]);
  

  return (
    <>
      {userCount < 2 && (
        <div>waiting for player to join</div>
      )}
      {userCount === 2 && (
        <div>Player joined, let's play</div>
      )}
    </>
  );
}

export default Game;
