import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import "/ap"

function UserList(props) {

    const { oponent, clickedUser, currentUser } = props;
    const [users, setUsers] = useState([]);
    useEffect(() => {
        // Send a request to the server to get the initial list of users
        axios.get('http://localhost:3000/users')
            .then((response) => {
                setUsers(response.data.filter((user) => user.isActive));
            })
            .catch((error) => {
                // Handle any errors
            });


    }, [users]);

    return (
        <ul class="user-list">
            {users.map((user) => {
                if (user.username !== currentUser.username) {
                    return (
                        <li class="user" key={user.userId}>
                            <img class="avatar" src={user.avatarUrl} alt={user.username} />
                            <div class="username">{user.username}</div>
                            <button class="send-message-button" onClick={() => clickedUser(user)}>
                                Send Message
                            </button>
                        </li>
                    );
                } else {
                    return null;
                }
            })}
        </ul>
    );
}

export default UserList;
